    [Test]
    public async Task TestCreateAndDeleteTag()
    {
        CreateTagRequest requestParams = new CreateTagRequest
        {
            Name = "TestTag"
        };
        await _tagService.CreateTag(requestParams);
        
        IActionResult jsonObject = await _tagService.GetTags();

        Tag tag = null;
        
        if (jsonObject is OkObjectResult ok)
        {
            string json = JsonConvert.SerializeObject(ok.Value);
            Console.WriteLine(json);
            Tag[] tags = JsonConvert.DeserializeObject<Tag[]>(json)!;
            tag = tags[0];
        }
        
        DeleteTagRequest deleteTagRequest = new DeleteTagRequest
        {
            TagUUID = tag.UUID.ToString()
        };
        
        IActionResult actionResult = await _tagService.DeleteTag(deleteTagRequest);
        
        var okResult = Assert.IsType<OkObjectResult>(actionResult);
    }
    
    [Test]
    public async Task TestUpdateImage()
    {
        CreateImageRequest imageRequest = new CreateImageRequest();
        imageRequest.Content = "";
        
        IActionResult actionResult = await _assetService.CreateImage(imageRequest);

        string imageid = "";

        CreateImageResponse imageResponse;
        
        if (actionResult is OkObjectResult ok)
        {
            imageResponse = ok.Value as CreateImageResponse;
            imageid = imageResponse.ImageId;
        }
        
        UpdateAssetRequest assetUpdateRequest = new UpdateAssetRequest
        {
            Content = "data:image/png;base64,"
        };
        
        IActionResult result = await _assetService.UpdateImage(imageid, assetUpdateRequest);
        
        Assert.IsType<OkObjectResult>(result);
    }

